INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (1, 1, '我还真是个不走运的男人啊。在作战结束返航的路上，竟然遇到这等的猎物。', '私もよくよく運のない男だな。作戦が終わっての帰り道で、あんな獲物に出会うなどとは', '0079', '01', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (2, 2, '真是不想承认啊。自己因为年轻而犯下的错误。', '認めたくないものだな。自分自身の、若さゆえの過ちというものを', '0079', '01', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (3, 3, '所谓作战，总是要比对方多看个几步才行。既然史立达顺利逃出来了。那么反过来，潜入进去不也是可行的吗。', '戦いとは、いつも二手三手先を考えて行うものだ。スレンダーは脱出した。ということは、逆もまた可能ではないのかな', '0079', '02', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (4, 4, '可、可是，如果是阿尔蒂西亚的话那，太、太强了。', 'し、しかし、アルティシアにしては、つ、強すぎる', '0079', '02', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (5, 5, '不要慌，乱动的话反而会被击中。像人类这么小的目标，可不会这么容易就被打中的。', '慌てるな。下手に動くとかえって当たる。人間みたいな小さな目標に、そうそう当たるもんじゃない', '0079', '02', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (6, 6, '就让我见识一下吧，联邦军的MS的性能。', '見せてもらおうか、連邦軍のモビルスーツの性能とやらを', '0079', '02', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (7, 7, '怎，怎么可能，应该是直接命中了啊。', 'ば、馬鹿な、直撃のはずだ', '0079', '02', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (8, 8, '只要不被击中的话，就没什么大不了的。快来掩护。', '当たらなければ、どうとういうことはない。援護しろ', '0079', '02', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (9, 9, '（要求了三台扎古结果就送来这些么。明明一点都不清楚敌人MS的性能）', '（3機のザクを要求してこれか。敵のモビルスーツの性能が皆目わからんのに）', '0079', '03', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (10, 10, '哼哼，就让我好好教教你，MS性能上的不同并不能决定战力上的差距这件事。', 'フフ、モビルスーツの性能の違いが、戦力の決定的差ではないということを教えてやる', '0079', '03', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (11, 11, '诶诶、联邦军的MS都是怪物吗。即便是这样的攻击都没有效果。', 'ええい、連邦軍のモビルスーツは化け物か。これだけの攻撃でも', '0079', '03', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (12, 12, '补给舰（パプア）被击毁了，加狄（ガデム）也死了。这到底是怎么回事。不管是MS也好，那艘船也好，很明显是败在了联邦军新兵器的高性能之下。这点我能理解。但是，这到底是怎么回事。那些家伙不仅战术一点都不成熟，连战斗的方式都像个外行人。', 'パプアがやられ、ガデムも死んだ。どういうことなのだ。モビルスーツにしろ、あの船にしろ、あきらかに連邦軍の新兵器の高性能の前に敗北を喫した。それはわかる。しかし、いったいどういうことなのだ。連中は戦法も未熟なら、戦い方もまるで素人だ', '0079', '03', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (13, 13, '敌人明明就在眼前却捕捉不到还真是件奇妙的事情啊。看来科学战发展到最后的话，又会倒回到过去视距内战斗的形式啊。', '敵を目の前にしても、捕捉されんとは奇妙なものだな。科学戦も詰まるところまで来てしまえば、大昔の有視界戦闘に逆戻りというわけだ', '0079', '04', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (14, 14, '杜雷（ドレン），你也变得会说话了嘛。', 'ドレン、貴様もいうようになったな', '0079', '04', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (15, 15, '（难不成之前的那个少女就是10年前分开的妹妹？不、如果真是阿尔蒂西亚的话就过勇敢了。是的，阿尔蒂西亚应该更温柔才对）', '（もしや、あの時の少女が10年前に別れた妹の、いや、アルティシアにしては強すぎる。そう、アルティシアはもっとやさしい）', '0079', '04', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (16, 16, '诶诶、驾驶技术好像又提高了，这高达的驾驶员。', 'ええい、腕が上がってきたようだな、このパイロットは', '0079', '05', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (17, 17, '克、克朗（クラウン），很遗憾，扎古时没有突入大气层的性能的。但是克朗，你的死不会白费。多亏了你吸引住了联邦军的MS，这下就有了击破他们的可能了。', 'ク、クラウン。ザクには大気圏を突破突破する性能突破する性能はない、気の毒だが。お前が連邦軍連邦軍のモビルスーツを引きつけてくれたおかげで、撃破することができるのだ', '0079', '05', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (18, 18, '战争可是无情的。这些事情当然在我的考虑之内。', '戦いは非情さ。そのくらいのことは考えてある', '0079', '05', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (19, 19, '这个称呼看来是承受不起了啊，卡尔玛扎比大佐', 'その呼び名は返上しなくちゃならんようだよ、がルマ・ザビ大佐', '0079', '05', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (20, 20, '别嘲笑我了，卡尔玛。不，应该称呼你地球方面军司令官卡尔玛扎比大佐吧', '言うなよがルマ。いや、地球方面軍司令官がルマ・ザビ大佐とお呼びすべきかな', '0079', '06', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (21, 21, '那就恭敬不如从命了。不过，我可以保证吉翁十字勋章是不会少的', 'お言葉に甘えよう。しかし、ジオン十字勲章ものであることは保証するよ', '0079', '06', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (22, 22, '卡尔玛会陷入苦战那是当然的。我们连续两次获取机密失败的理由就由他来给我们证明。而且，还是以远高于我们的战斗力', 'がルマが苦戦して当然さ。我々が二度ならず機密取りに失敗した理由をか彼が証明してくれている。しかも、我々以上の戦力でな', '0079', '06', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (23, 23, '（是吗，卡尔玛没有出击吗。还想着他要么与高达作战战死、要么在危险的时候由我来救他）', '（そうか、がルマは乗らなかったか。彼がガンダムと戦って死ぬもよし、危ういところを私が出て救うもよしと思っていたが）', '0079', '06', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (24, 24, '我不是说了么，相当于吉翁十字勋章的价值啊。你一定想着找下次的机会吧', '言ったさ、ジオン十字勲章ものだとな。次のチャンスを狙っているんだろ', '0079', '06', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (25, 25, '反正木马也是在胡乱射击。只要靠近敌人的战斗机，就不会被击中', 'どうせ木馬の当てずっぽうの攻撃だろう。敵の戦闘機に近づいてしまえ、当たりはしません', '0079', '07', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (26, 26, '地球上所谓的自由落体，还真不像说的那样自由啊', '地球での自由落下というやつは、言葉で言うほど自由ではないのでな', '0079', '07', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (27, 27, '什么、那、那么，我至今为止所见到的不过是敌人MS的性能的一部分吗？就、就已经那样了吗。那么、今后该如何战斗才好，卡尔玛。', '何。で、では、今まで私の見ていたのは、敵のモビルスーツの一部分の性能というわけなのか。あ、あれで。では、こ、今後どう戦ったらいいのだ、がルマ', '0079', '07', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (28, 28, '（看来，从小少爷般的作风已经深入骨髓了。真是天真啊）', '（どうも、お坊ちゃん育ちが身に染み込みすぎる。甘いな）', '0079', '08', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
