INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (37, 37, '你是个不错的朋友，但是你的父亲却不行啊。哼哼哼哼、哈哈哈哈', '君はいい友人であったが、君の父上がいけないのだよ。フフフフ、ハハハハハ', '0079', '10', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
