INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (39, 39, '啊、杜雷。记得把我的MS的电气系统给弄短路，做成无法使用的样子', 'あ、ドレン。私のモビルスーツは電気系統がめちゃめちゃに焼き切れていて、使えなかったことにしておけ', '0079', '11', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
