INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (41, 41, '看这样子，是基西莉亚的人吗？', 'においだな。キシリアの手の者か？', '0079', '12', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (42, 42, '不，木马的话还是想亲眼确认一下。可能会被基西莉亚大人嘲笑，但我也有固执的地方', 'いや、木馬ならこの目で確かめたい。キシリア殿に笑われようが、私にも意地というものがあるのでな', '0079', '26', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
