INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (47, 47, '联邦军的\"鲸鱼\"似乎比想象的要宽敞啊', '思ったより連邦軍の鯨は大きかったようだな', '0079', '28', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (48, 48, '哼哼哼哼、终于要找到查布罗最大的入口了。以木马消失的地点为中心仔细调查。连同查布罗基地一起端掉', 'フフフフ、ついにジャブローの最大の出入り口をつきとめたというわけさ。消えた地点を中心に徹底的に調査しろ。ジャブローの基地もろとも叩き潰してやる', '0079', '29', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (49, 49, '希望不只是虚有其表就好', '見かけ倒しでなけりゃいいがな', '0079', '29', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (50, 50, '又变得更强了啊，高达', 'さらにできるようになったな、ガンダム', '0079', '29', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (51, 51, '别开玩笑了', '冗談ではない', '0079', '29', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (52, 52, '离开军队吧，阿尔蒂西亚', '軍から身を引くいてくれないか、アルティシア', '0079', '30', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (53, 53, '不要停。停下的话能逃走的也逃不掉了，快走', '止まるな。とまったら助かるものも助からんぞ、走れ', '0079', '30', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (54, 54, '你是认真的吗？我们一旦掉头木马就会攻击。只能利用这个机会抢先发动攻击', '本気か。我々が背中を見せれば木馬が攻撃してくる。この機会に先制攻撃を仕掛けるしかない', '0079', '31', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (55, 55, '（阿尔蒂西亚、阿尔蒂西亚，但是，那个时候的阿尔蒂西亚确实穿着军装。那个聪明的，比别人更讨厌战争的阿尔蒂西亚居然在军队）', '（アルティシア、アルティシア、しかし、あのときのアルティシアは軍服を着いていた。聡明で、戦争を人一倍嫌っていたはずのアルティシアが）', '0079', '31', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (56, 56, '（阿尔蒂西亚、应该没在那上面吧）', '（アルティシア、乗っていないだろうな）', '0079', '31', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (57, 57, '好、已经忍了够久了。开火', 'よし、よく我慢した。撃て', '0079', '31', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (58, 58, '比起夹着尾巴逃跑，这样更不容易被击中。这点我可以保证', 'うろうろ逃げるより当たらんものだ。私が保証する', '0079', '31', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (59, 59, '不要急。他们就像是闯入我们院子的小鸟。我们还有机会', '焦るな。奴らは我々の庭に飛び込んだヒヨコだ。まだチャンスはある', '0079', '31', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (60, 60, '杜雷，你把我当什么了', 'ドレン、私を誰だと思っているのだ', '0079', '32', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (61, 61, '阿尔蒂西亚，我不能原谅扎比家。所以不要来妨碍我', 'アルティシア、私はザビけを許せないのだ。私の邪魔をしないでくれ', '0079', '32', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (62, 62, '希望您不要欺负年轻人。还请让我好好拜见一下您的手腕', '若者をいじめないでいただきたい。お手並みは拝見させていただく', '0079', '33', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (63, 63, '这也是无可奈何的。多兹尔中将也好、康斯空也好都只能看到眼前的敌人。在这点上基西莉亚大人就不同，战争的整体走向都看得一清二楚', 'やむを得んな。ドズル中将もコンスコンも目の前の敵しか見ておらん。その点キシリア殿は違う。戦争全体の行く末を見通しておられる', '0079', '34', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (64, 64, '阿姆罗。有点不可思议，像是听到过的名字', 'アムロ。不思議と知っているような名前だな', '0079', '34', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (65, 65, '是吗，真年轻啊。我知道眼前站着敌方士兵会让人手足无措，但是至少希望你能表示一下感谢，阿姆罗。', 'そうか、若いな。目の前に敵の兵士を置いて硬くなるのはわかるが、せめて礼ぐらいは言ってほしいものだな、アムロ君', '0079', '34', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (66, 66, '好好看着吧。实战可不是连续剧里那样光鲜亮丽的东西', 'よく見ておくのだな。実戦というのはドラマのように格好のよいものではない', '0079', '34', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (67, 67, '哼哼哼，拉拉真是聪明啊', 'フフフ、ララァは賢いな', '0079', '34', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (68, 68, 'SIDE6可以维持和平到现在，也和吉翁的态度有关。这一点请你好—好的考虑一下', 'サイド6が生き延びてこられたのも、ジオンの都合による。その辺をよーく考えるのだな', '0079', '35', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (69, 69, '就当做是我的妹妹吧', '私の妹、とでもしておいてもらおう', '0079', '35', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (70, 70, '我吗？拉拉，就不要再开我玩笑了', '私が？ララァ、冗談はやめにしてくれないか', '0079', '37', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (71, 71, '那是当然的。不然的话，我也不会把举目无亲的拉拉交给弗拉纳冈机关的。让你在SIDE6留下了一段寂寞的回忆真是抱歉', '当たり前だ。そうでなければ、みなしごだったララァをフラナガン機関に預けたりはしない。サイド6では、さびしい思いをさせてすまなかったな', '0079', '37', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (72, 72, '这是对我的嘲笑。不然的话，他可不会做出如此轻率的举动。不过，我也不可能就这么看着', '私への当てつけだよ。そうでなければ、彼がそんな軽率なことをするわけがない。しかし、黙って見ているわけにもいかんな', '0079', '37', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (73, 73, '我的主义是只要坐上MS就一定会回来。我是绝对不会想死的。所以战斗服也好一般的防护服也好都不会穿的', '私は、モビルスーツに乗っても必ず帰ってくる主義だ。死にたくない一心でな。だから戦闘服だのノーマルスーツなどは着ないのだよ', '0079', '37', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (74, 74, '这下知道了吧。装模作样的家伙能做什么', 'それ見たことか。付け焼き刃に何ができるというか', '0079', '37', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (75, 75, '你在说什么。拉拉可不只是个战士', '何を言うのだ。ララァはただの戦士ではない', '0079', '38', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (76, 76, '看来事情变得麻烦了。就再试一次看看高达的驾驶员是否真的是新人类', '厄介なことになりそうだ。ガンダムのパイロットもニュータイプだともう一度試してみるか', '0079', '38', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (77, 77, '因为我悟出了，只是让扎比家打倒联邦的话，人类是不会获取真的和平的', 'ザビ家を連邦が倒すだけでは、人類の真の平和は得られないと悟ったのだ', '0079', '38', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (78, 78, '嗯，要与那新人类为敌一点都不有趣。今后只能不择手段了', 'うん、そのニュータイプを敵にするのはおもしろくない。今後は手段を選べぬ、ということだ', '0079', '38', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (79, 79, '应该清楚我带着面具的理由吧。我已经把过去都抛去了', 'マスクをしているわけがわかるな。私は過去を捨てたのだよ', '0079', '38', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (80, 80, '真切地期望能遵守刚才的约定。致那温柔的阿尔蒂西亚索姆戴肯。来自卡斯巴尔雷姆戴肯，饱含着爱意', '「先の約束を果たされんことを切に願う。あの優しき、アルティシア・ソム・ダイクンヘ。キャスバル・レム・ダイクンより、愛をこめて」', '0079', '38', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (81, 81, '真是太厉害了。那份光辉竟然出自拉拉的手，就连我也难以相信。新人类的拉拉加上MA的艾尔美斯，竟有如此威力', 'すごいものだな。あの輝きがララァの仕掛けたものとは、この私にも信じられん。ニュータイプのララァとモビルアーマー・エルメス、これほどのものとは', '0079', '39', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (82, 82, '拉拉、战场上太过得意忘形的话可是会丢掉性命的', 'ララァ、戦場で調子に乗りすぎると命取りになるぞ', '0079', '39', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (83, 83, '我就作为忠告接受下来吧。看来我又增加一个朋友。请多指教，大尉', 'よい忠告として受け取っておこう。私はまた友人が増えたようだ。よろしく頼む、大尉', '0079', '39', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (84, 84, '拉拉，新人类并不是万能的。可能是因战争而诞生的，可悲的人类的变种', 'ララァ、ニュータイプは万能ではない。戦争の生み出した、人類の悲しい変種かもしれんのだ', '0079', '39', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (85, 85, '马里刚（マリガン），之后就拜托你了。你还欠我个人情啊。给我好好干哦', 'マリガン、あとを頼む。貴様には貸しがあったはずだ。ちゃんとやって見せろよ', '0079', '40', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (86, 86, '难道不是在嫉妒拉拉吗', 'ララァに嫉妬しているのではないのか', '0079', '40', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (87, 87, '高达、和之前的高达已经完全不同了', 'ガンダム、昨日までのガンダムとまるで違うぞ', '0079', '40', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (88, 88, '自从被调到基西莉亚大人的手下，总想着会有这么一天，但真的到了却又害怕了起来。手的颤抖根本停不下来', 'キシリア様に呼ばれたときから、いつかこのようなときが来ると思っていましたが、いざとなると恐いものです。手の震えが止まりません。', '0079', '41', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (89, 89, '现在是拉拉你更优秀', '今はララァのほうが優れている', '0079', '41', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (90, 90, '拉拉、不要在和那家伙废话了', 'ララァ、奴とのざれごとはやめろ', '0079', '41', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (91, 91, '拉拉、不能放手', 'ララァ、手を放すわけにはゆかん', '0079', '41', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (92, 92, '把话给我说清楚，真让人不爽', 'はっきり言う。気にいらんな', '0079', '42', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (93, 93, '那么问题就是，我是不是真的拥有新人类的潜力了', 'さて問題は、私に明確なニュータイプの素養があるかどうかだが', '0079', '42', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (94, 94, '看见了，我也能看见敌人了', '見えるぞ、私にも敵が見える', '0079', '42', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (95, 95, '但是，我也应该是新人类啊', 'しかし、私もニュータイプのはずだ', '0079', '42', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (96, 96, '拉拉，请告诉我。到底该怎么做才好？', 'ララァ、教えてくれ。どうしたらいいのだ？', '0079', '43', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (97, 97, '这份力量，或许是拉拉给我的也说不定，真是要感激她。', 'その力、ララァが与えくれたかもしれんのだ、ありがたく思うのだな', '0079', '43', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (98, 98, '现在，像你这样的新人类过于危险了。所以我要杀了你', '今、君のようなニュータイプは危険すぎる。私は君を殺す', '0079', '43', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (99, 99, '是的。身体上的技能就算是新人类的话，只要没训练过也和普通人一样', 'そう、体を使う技はニュータイプといえども訓練をしなければ', '0079', '43', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (100, 100, '在战场上会成为强力的武器。这也是没有办法的事', '戦場では強力な武器になる。やむを得んことだ', '0079', '43', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (101, 101, '打倒扎比家已经是顺带的事情了，阿尔蒂西亚。在没有了吉翁之后便是新人类的时代了。阿姆罗你如果明白我所说的，就成为我的伙伴吧。这样拉拉也会高兴的', 'ザビ家打倒なぞ、もうついでの事なのだ、アルティシア。ジオンなきあとはニュータイプの時代だ。アムロ君がこの私の言うことがわかるのなら、私の同志になれ。ララァも喜ぶ', '0079', '43', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (102, 102, '没有头盔的话就已经死了。', 'ヘルメットがなければ即死だった', '0079', '43', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (103, 103, '你也已经是大人了，忘掉战争吧。做一个好女人。阿姆罗在叫你。', 'お前ももう大人だろ、戦争も忘れろ。いい女になるのだな。アムロ君が呼んでいる', '0079', '43', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
INSERT INTO `gundam_lines` (`id`, `order`, `line`, `line_jp`, `series`, `episode`, `person`, `person_jp`) VALUES (104, 104, '（卡尔玛，这是我的饯别礼。愿你能和你姐姐好好相处）', '（がルマ。私の手向けだ。姉上と仲よく暮らすがいい）', '0079', '43', '夏亚·阿兹纳布尔', 'シャア・アズナブール');
